trigger.script:
if QuestStatus("FindSergoBody_quest")==Q_COMPLETED then local Pl = GetPlayerVehicle()
if Pl then Pl:SetThrottle( 0 ) Pl:SetCustomLinearVelocity( 0 )
local b = SpawnMessageBox( "8801" )
if b == 1 then PassToMap("r4m1", "FromR4M0", -1 ) GL_PassToMap = true Pl:SetCustomControlEnabled( true ) else Pl:SetGamePositionOnGround(CVector(getPos("FromR4M1"))) Pl:SetRotation(Quaternion(-0.081, -0.019, -0.007, -0.997)) TActivate("R4M1ExitRefuse_rolik") end end else SpawnMessageBox("8002")
local Pl = GetPlayerVehicle()
if Pl then Pl:SetThrottle( 0 ) Pl:SetCustomLinearVelocity( 0 )
Pl:SetGamePositionOnGround(CVector(getPos("FromR4M1"))) Pl:SetRotation(Quaternion(-0.081, -0.019, -0.007, -0.997)) TActivate("R4M1ExitRefuse_rolik") end end

if QuestStatus("GoToOracle_quest")==Q_TAKEN then local Pl = GetPlayerVehicle()
if Pl then Pl:SetThrottle( 0 ) Pl:SetCustomLinearVelocity( 0 )
local b = SpawnMessageBox( "8801" )
if b == 1 then PassToMap("r1m1", "FromR4M0", -1 ) GL_PassToMap = true Pl:SetCustomControlEnabled( true ) else Pl:SetGamePositionOnGround(CVector(getPos("FromR1M1"))) Pl:SetRotation(Quaternion(0.048, 0.982, 0.052, -0.173)) TActivate("R1M1ExitRefuse_rolik") end end else local Pl = GetPlayerVehicle() if Pl then Pl:SetGamePositionOnGround(CVector(getPos("FromR1M1"))) Pl:SetRotation(Quaternion(0.048, 0.982, 0.052, -0.173)) TActivate("R1M1ExitRefuse_rolik") end end
